import { useState, useEffect } from 'react';
import './App.scss';

let quoteDB = "https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json"

function App() {
  const [quote, setQuote] = useState("If you do what you’ve always done, you’ll get what you’ve always gotten.");
  const [author, setAuthor] = useState("Gabriel Mack");
  const [randomNumber, setRandomNumber] = useState(0);
  const [quotes, setQuotes] = useState(null);

  const fetchQuotes = async(url) => {
    const response = await fetch(url);
    const parsedJSON = await response.json();
    setQuotes(parsedJSON.quotes);
  }

  useEffect(() => {
    fetchQuotes(quoteDB)
  }, [quoteDB])


  const changeQuoteAndAuthor = () => {
    setQuote("balls");
    setAuthor("Ryan Barros");
  }

  const genRandomNumber = () => {
    setQuote(quotes[Math.floor(Math.random() * quotes.length)].quote);
    setAuthor(quotes[Math.floor(Math.random() * quotes.length)].author);
  }

  return (
    <div className="App">
      <header className="App-header">
        <div id="quote-box">
        <h1>Random Number: {randomNumber}</h1>
        <button onClick={() => setRandomNumber(genRandomNumber())}>Generate a random quote</button>
        <p id="text">
          {quote}
        </p>
        <p id="author">
          {author}
        </p>
        <button onClick={() => changeQuoteAndAuthor()}>Change Quote</button>
        </div>
      </header>
    </div>
  );
}

export default App;
